document.addEventListener("DOMContentLoaded",()=>{const t=document.getElementById("message"),n=document.getElementById("showOverlay");n.addEventListener("click",()=>{const e=t.value.trim();e&&chrome.tabs.query({active:!0,currentWindow:!0},s=>{s[0]&&chrome.tabs.sendMessage(s[0].id,{type:"showMessage",content:e,style:{position:"top",color:"white",fontSize:"24px",duration:5e3}})})}),t.addEventListener("keypress",e=>{e.key==="Enter"&&n.click()})});
